import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button } from "react-bootstrap";

const MarketWrite = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [img, setImg] = useState({
      image_file: "",
      preview_URL: "img/default_image.png"
    });


    const saveImage = (e) => {
      e.preventDefault();
      if(e.target.files[0]){
        // 새로운 이미지를 올리면 createObjectURL()을 통해 생성한 기존 URL을 폐기
        URL.revokeObjectURL(img.preview_URL);
        const preview_URL = URL.createObjectURL(e.target.files[0]);
        setImg(() => (
          {
            image_file: e.target.files[0],
            preview_URL: preview_URL
          }
        ))
      }
    }

    const deleteImage = () => {
      URL.revokeObjectURL(img.preview_URL);
      setImg({
        image_file: "",
        preview_URL: "img/default_image.png"
      })
    }

    useEffect(() => {
      return () => {
        URL.revokeObjectURL(img.preview_URL)
      }
    }, [])

    const onSubmit = async () => {
        if(img.image_file){
          const formData = new FormData();
          formData.append("file", img.image_file);

          let dataSet = {
            title,
            content,
          };

        const json = JSON.stringify(dataSet)
        const blob = new Blob([json], { type: "application/json" });
        formData.append("request", blob); 


        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        await axios.post("/api/v1/boards", formData, {
              headers: {
              "Content-Type": "multipart/form-data", 
          }})

              window.location.href = "/market";
              alert('등록되었습니다.');
            setImg({
              image_file: "",
              preview_URL: "img/default_image.png"
            });
      }
    }

    return (
      <form >
          제 목 <input 
          value={title}
          onChange={(e)=> {setTitle(e.target.value)}}
          type="text"
          placeholder="제목을 입력하세요"
      /><br/>
      내 용 <input 
          value={content}
          onChange={(e)=> {setContent(e.target.value)}}
          type="text"
          placeholder="내용을 입력하세요"
      /><br/>

        <input 
          type="file"
          accept="image/*"
          onChange={saveImage}
          onClick={(e) => e.target.value = null}
        />

        <div>
          <img src={img.preview_URL} width="20%" height="200px" />
        </div>

        <div className="upload-button">

        <Button color="error" variant="contained" onClick={deleteImage}>
        <i class="fa-solid fa-xmark"></i>
        </Button>
        <Button color="success" variant="contained" onClick={onSubmit}>
          제출
        </Button>
      </div>
      </form>
  );
}

export default MarketWrite;
