import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';


function MarketDetail() {

    const [data, setData] = useState([]);

    useEffect(() => {
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.get(`/api/v1/boards/${boardsId}/detail`)

        .then((response)  => { 
          setData(response.data);
          console.log(response);
        })

      }, [])

    const {boardsId} = useParams();

    return (
        <div className="w-full h-full flex justify-center items-center">
        {data && (

            <>
            <div className="w-[500px] h-[500px] flex px-6 py-4 border border-black mt-16">

                <div className="w-5/12 flex flex-col space-y-4">
                <h1 className="text-black font-semibold font-Inter text-2xl">{data.title}</h1>
                </div>
            </div>

            </>


            )
        }
        </div>
    )
}

export default MarketDetail;
