  return call("/auth/logout", "GET").then((response) => {
    response.json();
    console.log(response);
    if (response.headers.get("access")) {

    axios.defaults.headers.common[
      "accessToken"
    ] = `Bearer ${response.headers.get("access")}`;
  }
    localStorage.removeItem("access");
