import React, {useState} from "react";
import "./Navbar.css"
import Dropdown from "./Dropdown";
import {Link, Router} from "react-router-dom";
import { call, signout } from "../../service/ApiService"


function Navbar() {
    const [click, setClick] = useState(false)
    const [dropdown, setDropdown] = useState(false)

    const handleClick = () => setClick(!click)
    const closeMobileMenu = () => setClick(false);
    const onMouseEnter = () => {
        if(window.innerWidth < 960) {
            setDropdown(false)
        } else {
            setDropdown(true)
        }
    };

    const onMouseLeave = () => {
        if(window.innerWidth < 960) {
            setDropdown(false)
        } else {
            setDropdown(false)
        }
    };

    return (
        <>
            <nav className="navbar">
                <Link to="/" className="navbar-logo">
                    Greener
                </Link>
                <div className="menu-icon" onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className="nav-item">
                        <Link to='/recommand' className='nav-links' onClick={closeMobileMenu}>
                            식물추천
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to='/hotel' className='nav-links' onClick={closeMobileMenu}>
                            식물호텔
                        </Link>
                    </li>
                    <li className="nav-item"
                        onMouseEnter={onMouseEnter}
                        onMouseLeave={onMouseLeave}
                    >
                        <Link to='/community' className='nav-links' onClick={closeMobileMenu}>
                            커뮤니티 <i className="fas fa-caret-down" />
                        </Link>
                        {dropdown && <Dropdown />}
                    </li>
                    <li className="nav-item">
                        <Link to='/mypage' className='nav-links' onClick={closeMobileMenu}>
                            마이페이지 <i className="fas fa-caret-down" />
                        </Link>
                        {dropdown && <Dropdown />}
                    </li>
                    <li className="nav-item">
                        <Link to='/notice' className='nav-links' onClick={closeMobileMenu}>
                            공지사항
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link to='/faq' className='nav-links' onClick={closeMobileMenu}>
                            FAQ
                        </Link>
                    </li>

                </ul>
                <Link to="/login">
                    <button>로그인</button>
                </Link>
                <button color="inherit" onClick={signout}>
                    로그아웃
                </button>
            </nav>
        </>

    )
}

export default Navbar;
