import React, {Component, useState} from "react";
import "./Navbar.css"
import Dropdown from "./Dropdown";
import {Link, Router} from "react-router-dom";


function Navbar() {
    const [click, setClick] = useState(false)
    const [dropdown, setDropdown] = useState(false)

    const handleClick = () => setClick(!click)
    const closeMobileMenu = () => setClick(false);
    const onMouseEnter = () => {
        if(window.innerWidth < 960) {
            setDropdown(false)
        } else {
            setDropdown(true)
        }
    };

    const onMouseLeave = () => {
        if(window.innerWidth < 960) {
            setDropdown(false)
        } else {
            setDropdown(false)
        }
    };

    return (
        <>
            <nav className="navbar">
                <Link href="/" className="navbar-logo">
                    Greener
                </Link>
                <div className="menu-icon" >
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className="nav-item">
                        <Link href='/recommand' className='nav-links' >
                            식물추천
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link href='/hotel' className='nav-links' >
                            식물호텔
                        </Link>
                    </li>
                    <li className="nav-item"
                        onMouseEnter={onMouseEnter}
                        onMouseLeave={onMouseLeave}
                    >
                        <Link href='/community' className='nav-links' >
                            커뮤니티 <i className="fas fa-caret-down" />
                        </Link>
                        {dropdown && <Dropdown />}
                    </li>
                    <li className="nav-item">
                        <Link href='/mypage' className='nav-links' >
                            마이페이지 <i className="fas fa-caret-down" />
                        </Link>
                        {dropdown && <Dropdown />}
                    </li>
                    <li className="nav-item">
                        <Link href='/notice' className='nav-links' >
                            공지사항
                        </Link>
                    </li>
                    <li className="nav-item">
                        <Link href='/faq' className='nav-links' >
                            FAQ
                        </Link>
                    </li>

                </ul>

            </nav>
        </>

    )
}

class Nav extends React.Component {
    render() {
        return (
            <Navbar />
        )
    }
}
export default Nav;
