//     alert('로그아웃 되었습니다.');
// }

export function logout(userDto) {
  return call("/auth/logout", "GET", userDto).then((request) => {
    localStorage.removeItem("access")
    if (request.headers.get("access")) {
        console.log(request.headers.get("access"));
      // 로컬 스토리지에 토큰 저장
      axios.defaults.headers.common[
        "accessToken"
      ] = `Bearer ${request.headers.get("access")}`;
    }
    window.location.href = "/login";
    alert('로그아웃 되었습니다.')     

    })
};


