import React, { useState, useEffect } from 'react';
import MarketItem from './MarketItem';
import axios from 'axios';
// import './list.css'

function useFetch(url) {

    const [data, setData] = useState([]);

    async function fetchUrl() {
        const response = await fetch(url);
        const json = await response.json();

        setData(json);
    }

    useEffect(() => {
      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        fetchUrl();
    }, []);
    return data;
}


function Market() {

    const data = useFetch("/api/v1/boards");

    return (
        <main className="list-template">     
            <div className="list-title">
                폼 게시판
            </div>
            <section className="head-wrapper">
                <span>번호</span>
                <span className="title-column">제목</span>
                <span>내용</span>
                <span>작성일</span>
                <span>작성자</span>
            </section>
            <section className="list-wrapper">
                {data.map(
                    ({boardsId, title, content, createDate, nickName}) => (
                        <MarketItem
                            boardsId={boardsId}
                            title={title}
                            content={content}
                            createDate={createDate}
                            nickName={nickName}
                            key={boardsId}
                        />
                    )
                )}
            </section>
        </main>
    );
}

export default Market;
