import React, {useState} from "react";
import axios from "axios";


function MarketWrite() {

    const [files, setFiles] = useState("");
    const onLoadFile = (e) => {
        const file = e.target.files;
        console.log(file);
        setFiles(file);
    };

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [imagePath, setImagePath] = useState("");

    const data = {
        title: title,
        content: content,
        imagePath: imagePath
    }

    function Submit(e) {
        e.preventDefault();

        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.post("/api/v1/boards", data)     
            .then((response) => {
                  window.location.href = "/market";
                  alert('등록되었습니다.')
            });
    }

return (
    <>
    <h1>글쓰기</h1>
    <form>
    제 목 <input 
        value={title}
        onChange={(e)=> {setTitle(e.target.value)}}
        type="text"
        placeholder="제목을 입력하세요"
    /><br/>
    내 용 <input 
        value={content}
        onChange={(e)=> {setContent(e.target.value)}}
        type="text"
        placeholder="내용을 입력하세요"
    /><br/>
    <div className="upload_img">
        <div className="custom_img">
            <strong>
                업로드된 이미지        
            </strong>
            <div className="img">
                <img src="" alt="" />
            </div>
        </div>    
    </div>
    <form className="upload_input">
         <input 
        value={imagePath}
        onChange={onLoadFile}
        type="file"
        accept="img/*"

    />
    <label htmlFor="imgae">파일 선택하기</label>
    </form>
  <br/>

    <button onClick={Submit}>글쓰기</button>
    </form>
    </>
)
}

export default MarketWrite;
