import { API_BASE_URL } from "../app-config";
import axios from "axios";


export function call(api, method, request) {
  let headers = new Headers({
    "Content-Type": "application/json",
  }
  );

  // 로컬 스토리지에서 ACCESS TOKEN 가져오기
  const accessToken = localStorage.getItem("access_token");

  if (accessToken && accessToken !== null) {
    // console.log(accessToken);
    headers.append("Authorization", "Bearer " + accessToken);

  }

  let options = {
    headers: headers,
    url: API_BASE_URL + api,
    method: method
  };

  if (request) {
    // GET method
    options.body = JSON.stringify(request);

  }

  return fetch(options.url, options)
    .then((response) =>

       {

        if (!response.ok) {
          // response.ok가 true이면 정상적인 리스폰스를 받은것, 아니면 에러 리스폰스를 받은것.
          return Promise.reject(response);
        }
        return response;
       }
    )
    .catch((error) => {
      // 추가된 부분
      if (error.status === 403) {
        // window.location.href = "/login"; // redirect
      }
      return Promise.reject(error);
    });
}

export function login(userDto) {
  return call("/auth/login", "POST", userDto).then((response) => {

    console.log(response);
    console.log(response.headers.get("accessToken"));
    if (response.headers.get("access_token")) {
      console.log(response)
      // 로컬 스토리지에 토큰 저장
      axios.defaults.headers.common[
        "accessToken"
      ] = `Bearer ${response.headers.get("access_token")}`;

      localStorage.setItem("access_token", response.headers.get("access_token"));

      // token이 존재하는 경우 Todo 화면으로 리디렉트
      window.location.href = "/";
      alert('로그인 되었습니다.')
    }
  });
}


export function signout() {

    localStorage.removeItem("access_token");
    window.location.href = "/login";
    alert('로그아웃 되었습니다.');
}

