import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import axios from "axios";

class Market extends Component {
    constructor(props) {
        super(props);

        this.state = {
            responseSwtoolList: '',
            append_SwtoolList: '',

        }
    }


    componentDidMount() {
        this.callSwToolListApi()
    }

    callSwToolListApi = async () => {
      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.get('/api/v1/boards', {
        })
        .then( response => {
          console.log(response);
            try {
                this.setState({ responseSwtoolList: response });
                this.setState({ append_SwtoolList: this.SwToolListAppend() });
            } catch (error) {
                alert('작업중 오류가 발생하였습니다.');
            }
        })
        .catch( error => {alert('작업중 오류가 발생하였습니다.');return false;} );

    }

    SwToolListAppend = () => {
        let result = []
        var SwToolList = this.state.response
        console.log(SwToolList);

        for(let i=0; i<SwToolList.json.length; i++){
            var data = SwToolList.json[i]
            console.log(data);

            // var date = data.reg_date
            // var year = date.substr(0,4)
            // var month = date.substr(4,2)
            // var day = date.substr(6,2)
            // var reg_date = year +'.'+month+'.'+day

            //                             boardsId={boardsId}
//                             title={title}
//                             content={content}
//                             createDate={createDate}
//                             nickName={nickName}
//                             key={boardsId}


            result.push(
                <tr className="hidden_type">
                    <td>{data.boardsId}</td>
                    <td>{data.title}</td>
                    <td>{data.content}</td>

                    <td>{data.nickName}</td>
                    <td>
                        <Link to={'/MarketDetail/'+data.boardsId} 
                        className="bt_c1 bt_c2 w50_b">수정</Link>
                        <a href="#n" className="bt_c1 w50_b" >삭제</a>
                    </td>
                </tr>
            )
        }
        return result
    }

    render () {
        return (
            <section className="sub_wrap" >
                <article className="s_cnt mp_pro_li ct1 mp_pro_li_admin">
                    <div className="li_top">
                        <h2 className="s_tit1">Software Tools 목록</h2>
                        <div className="li_top_sch af">
                        <Link to={'/Marketwrite'} className="sch_bt2 wi_au">Tool 등록</Link>
                        </div>
                    </div>

                    <div className="list_cont list_cont_admin">
                        <table className="table_ty1 ad_tlist">
                            <tr>
                                <th>번호</th>
                                <th>제목</th>
                                <th>내용</th>

                                <th>등록자</th>
                            </tr>
                        </table>	
                        <table className="table_ty2 ad_tlist">
                            {this.state.append_SwtoolList}
                        </table>
                    </div>
                </article>
            </section>
        );
    }
}

export default Market;
