import React from "react";
import { Form, FormGroup, Label, Col, Input, FormText, Button } from "reactstrap";
import { myplantswrite } from "../service/ApiService";


class MyplantsWrite extends React.Component {
    constructor(props) {
      super(props);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      event.preventDefault();
      // 오브젝트에서 form에 저장된 데이터를 맵의 형태로 바꿔줌.
      const data = new FormData(event.target);
      const imagePath = data.get("imagePath");
      const name = data.get("name");
      const bornDate = data.get("bornDate");
      myplantswrite({ imagePath: imagePath, name: name, bornDate: bornDate }).then(
        (response) => {
          // 계정 생성 성공 시 login페이지로 리디렉트
          window.location.href = "/scheduler";
          alert('등록되었습니다.')
        }
      );
    }

     todayTime = () => {
        let now = new Date();  // 현재 날짜 및 시간
        let todayMonth = now.getMonth() + 1;
        let todayDate = now.getDate()
        const week = ['일', '월', '화', '수', '목', '금', '토'];
        let dayOfWeek = week[now.getDay()];
        let hours = now.getHours()
        let minutes = now.getMinutes();

        return todayMonth + '월 ' + todayDate + '일 ' + dayOfWeek + '요일 ' 
        + hours + '시 ' + minutes + '분';
       }

    render() {



      return (
            <>
                <Form noValidate onSubmit={this.handleSubmit}>
                <FormGroup row>
                    <Label
                    for="myplants"
                    sm={2}
                    >
                    식물 이름
                    </Label>
                    <Col sm={10}>
                    <Input
                        id="name"
                        name="name"
                        placeholder="나의 식물 이름"
                        type="text"
                    />
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label
                    for="examplePassword"
                    sm={2}
                    >
                    등록일
                    </Label>
                    <Col sm={10}>
                    <Input>{this.todayTime}</Input>

                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label
                    for="exampleText"
                    sm={2}
                    >
                    Text Area
                    </Label>
                    <Col sm={10}>
                    <Input
                        id="exampleText"
                        name="text"
                        type="textarea"
                    />
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label
                    for="imagePath"
                    sm={2}
                    >
                    File
                    </Label>
                    <Col sm={10}>
                    <Input
                        id="imagePath"
                        name="file"
                        type="file"
                    />
                    </Col>
                </FormGroup>
                    <Button>
                        Submit
                    </Button>
                </Form>
            </>
      );
    }
  }





export default MyplantsWrite;
