import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { Button } from "react-bootstrap";

const MarketChange = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [img, setImg] = useState({
      image_file: "",
      preview_URL: "image/default_image.png"
    });

    const {boardsId} = useParams();

    useEffect(() => {
      const getBoard = async () => {
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`
        const {data} = await axios.get(`/api/v1/boards/${boardsId}/detail`)
        return data
      }
      getBoard()
        .then((result)  => { 
          setTitle(result.title);
          setContent(result.content);
          setImg(result.img);
          console.log(result.img)
        })

      }, [])

    // useEffect(One, [img])

      const data = {
        title: title,
        content: content,
        img: img
    }

      function Update(e) {
        e.preventDefault()

        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.put(`/api/v1/boards/${boardsId}`, data)
        .then(
            window.location.href = "/market",
            alert('수정되었습니다.')
        )
      }

      // const FileDelete = () => {
      //   response.data.img = ""
      //   console.log(data.img)
      // }


return (
    <>
    <h1>수정하기</h1>
    <form>
    제 목 <input 
        value={title}
        onChange={(e)=> {setTitle(e.target.value)}}
        type="text"
    /><br/>
    내 용 <input 
        value={content}
        onChange={(e)=> {setContent(e.target.value)}}
        type="text"
    /><br/>
    이미지 <img 
        src={img}
        alt=""
        width="20%" height="200px"
        // placeholder="이미지를 등록하세요"
        // onChange={(e)=> {setImg(e.target.value)}}
    />
    {/* <button onClick={() => FileDelete()}>삭제</button> */}

    <input 
          type="file"
          multiple="multiple"
          onChange={(e) => {setImg(e.target.value)}}
        /><br/>
  <Button href={`/marketdetail/${boardsId}`} variant="outline-secondary">&nbsp; 취소 &nbsp;</Button>
  <Button onClick={Update} variant="outline-success">&nbsp; 수정 &nbsp;</Button>

    </form>
    </>
    )
}

export default MarketChange;
