import React from 'react';
import axios from 'axios';

function MyplantsWrite() {
  axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
    return (
        <div className='Write'>

            <form action="/api/v1/my-plants" method="post">

                <div>
                	<input type='text' id='name' name='이름' placeholder='식물이름'/> 
                </div>
                <div>
                	<input type='url' id='imagePath' name='url' placeholder='이미지'/> 
                </div>

                <div>
                    등록일&nbsp;&nbsp;
                        <input type="date" name="등록일" id='bornDate'/>
                    	&nbsp;&nbsp; 
                </div>
                <div id="submit_btn">
                    <button type="submit">저장</button>&nbsp;&nbsp;
                    <button>취소</button>
                </div>
            </form>
        </div>
    );
}

export default MyplantsWrite;
