        function Scheduler() {
          const [list, setList] = useState(null);
          const [loading, setLoading] = useState(false);
          const [error, setError] = useState(null);

          useEffect(() => {
            const fetchList = async () => {
              try {
                // 요청이 시작 할 때에는 error 와 users 를 초기화하고
                setError(null);
                setList(null);
                // loading 상태를 true 로 바꿉니다.
                setLoading(true);

                const response = await axios.get(

                  'http://localhost:8080/api/v1/my-plants',
                  axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`

                );

                setList(response.data); // 데이터는 response.data 안에 들어있습니다.
              } catch (e) {
                setError(e);
              }
              setLoading(false);
            };

            fetchList();
          }, []);

          if (loading) return <div>로딩중..</div>;
          if (error) return <div>에러가 발생했습니다</div>;
          if (!list) return null;
          return (
            <ul>
              {list.map(list => (
                <li key={list.id}>
                  {list.id} {list.name} {list.bornDate} {list.imagePath}
                </li>
              ))}
            </ul>
          );
        }

        export default Scheduler;
