import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

const MarketWrite = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [tempFile, setTempFile] = useState("");
    // const [boardtype, setBoardtype] = useState("");


    const onSubmit = async (e) => {
        e.preventDefault();


        const formData = new FormData();
        formData.append("file",tempFile[0]);


        let dataSet = {
            title,
            content,
        };


        //
      formData.append("request", JSON.stringify(dataSet)); 
      //500에러 다시봉줘 500에러 없어졌어요저장하고 한거마즘/
//ssss 탭 보여줘 preview 자세히 보게 500뜸 이번엔엔 저희 requestBody 가 아니라 RequestPart 로 받는데요 RequestPart ? requestPart
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
      axios.post("/api/v1/boards", {
        requestPart: {
          'file': tempFile[0], 
          'request': {
            'title':title,
            'content':content,
          },
          }

        }, {
            headers: {
            "Content-Type": "multipart/form-data",
        }});
    }


      return (
    <form onSubmit={onSubmit}>
        제 목 <input 
         value={title}
         onChange={(e)=> {setTitle(e.target.value)}}
         type="text"
         placeholder="제목을 입력하세요"
     /><br/>
     내 용 <input 
         value={content}
         onChange={(e)=> {setContent(e.target.value)}}
         type="text"
         placeholder="내용을 입력하세요"
     /><br/>

      <input 
        type="file"
        multiple="multiple"
        onChange={(e) => {setTempFile(e.target.files)}}
      /><br/>

      <button type="submit">제출</button>
    </form>
  );
}


export default MarketWrite;

