import axios from 'axios';

//로그인
export const login = ({email, password}) =>
axios({
    method: 'post',
    url: '/auth/signin',
    data: {email, password}
});

//회원가입
export const register = ({email, password, username}) =>
axios({
    method: 'post',
    url: '/auth/signup',
    data: {email, password, username}
});

